package Lec2;

import java.util.Scanner;

public class Pattern_0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int star = n;
		int a = 1;
		while (a <= star) {
			System.out.print("* ");
			a++;
		}
	}

}
